using IronSoftware.Drawing;
using SixLabors.ImageSharp.Processing;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

using SKBitmap = SixLabors.ImageSharp.Image;
using SKRectangle = IronSoftware.Drawing.Rectangle;

namespace YoutubeTicker
{
    public static class StaticImages
    {

        public static AnyBitmap Status_Hot = GetBitmap("");

        public static AnyBitmap Status_Hot_2 = null;

        public static AnyBitmap Status_New = GetBitmap("");

        public static AnyBitmap Status_New_2 = null;

        public static AnyBitmap Status_Played = GetBitmap("");

        public static AnyBitmap Status_Played_2 = null;

        public static AnyBitmap Loading = GetBitmap("");

        public static AnyBitmap Loading_Transparent = GetBitmap("");

        public static AnyBitmap Loading_Transparent_30 = null;

        public static AnyBitmap No_Internet = GetBitmap("");

        //public static AnyBitmap No_Internet_Red = GetBitmap("");

        public static AnyBitmap Christmas = GetBitmap("");

        public static AnyBitmap Sylvester = GetBitmap("");

        public static AnyBitmap NewYears = GetBitmap("");

        public static AnyBitmap Now_Live_1 = GetBitmap("");

        public static AnyBitmap Now_Live_2 = GetBitmap("");

        public static AnyBitmap Now_Live_3 = GetBitmap("");

        public static AnyBitmap Now_Live_4 = GetBitmap("");

        static StaticImages()
        {
            SKBitmap bmp = Loading_Transparent.Clone();
            bmp.Mutate(a => a.Resize(100, 100));
            Loading_Transparent_30 = bmp;


            bmp = Status_Hot.Clone();
            bmp.Mutate(a => a.Resize(bmp.Width / 2, bmp.Height / 2));
            Status_Hot_2 = bmp;

            bmp = Status_New.Clone();
            bmp.Mutate(a => a.Resize(bmp.Width / 2, bmp.Height / 2));
            Status_New_2 = bmp;

            bmp = Status_Played.Clone();
            bmp.Mutate(a => a.Resize(bmp.Width / 2, bmp.Height / 2));
            Status_Played_2 = bmp;
        }

        private static AnyBitmap GetBitmap(String base64)
        {
            if(string.IsNullOrEmpty(base64))
            {
                return new AnyBitmap(1, 1, Color.Black);
            }

            byte[] bytes = Convert.FromBase64String(base64);

            return new AnyBitmap(bytes);
        }

    }
}
